	 * an identical criteria, it returns the collection.
	 * Otherwise if this Room is new, it will return
	 * an empty collection; or if this Room has previously
	 * been saved, it will retrieve related ZoneHasRooms from storage.
	 *
	 * This method is protected by default in order to keep the public
	 * api reasonable.  You can provide public methods for those you
	 * actually need in Room.
	 */
	public function getZoneHasRoomsJoinZone($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		if ($criteria === null) {
			$criteria = new Criteria(RoomPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collZoneHasRooms === null) {
			if ($this->isNew()) {
				$this->collZoneHasRooms = array();
			} else {

				$criteria->add(ZoneHasRoomPeer::ROOM_ID, $this->id);

				$this->collZoneHasRooms = ZoneHasRoomPeer::doSelectJoinZone($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(ZoneHasRoomPeer::ROOM_ID, $this->id);

			if (!isset($this->lastZoneHasRoomCriteria) || !$this->lastZoneHasRoomCriteria->equals($criteria)) {
				$this->collZoneHasRooms = ZoneHasRoomPeer::doSelectJoinZone($criteria, $con, $join_behavior);
			}
		}
		$this->lastZoneHasRoomCriteria = $criteria;

		return $this->collZoneHasRooms;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collCloseperiods) {
				foreach ((array) $this->collCloseperiods as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collDayperiods) {
				foreach ((array) $this->collDayperiods as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collRoomprofiles) {
				foreach ((array) $this->collRoomprofiles as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collRoomHasActivitys) {
				foreach ((array) $this->collRoomHasActivitys as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collRoomHasEnergyactions) {
				foreach ((array) $this->collRoomHasEnergyactions as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collRoomHasFeaturevalues) {
				foreach ((array) $this->collRoomHasFeaturevalues as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collZoneHasRooms) {
				foreach ((array) $this->collZoneHasRooms as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collCloseperiods = null;
		$this->collDayperiods = null;
		$this->collRoomprofiles = null;
		$this->collRoomHasActivitys = null;
		$this->collRoomHasEnergyactions = null;
		$this->collRoomHasFeaturevalues = null;
		$this->collZoneHasRooms = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseRoom:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseRoom::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BaseRoom
