					$o->clearAllReferences($deep);
				}
			}
			if ($this->collUsergroupHasChiefs) {
				foreach ((array) $this->collUsergroupHasChiefs as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collUsergroupHasUsers) {
				foreach ((array) $this->collUsergroupHasUsers as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collUserHasRoles) {
				foreach ((array) $this->collUserHasRoles as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collMessagesRelatedByRecipientId = null;
		$this->collMessagesRelatedBySenderId = null;
		$this->collMessagesRelatedByOwnerId = null;
		$this->collReservations = null;
		$this->collReservationOtherMemberss = null;
		$this->collSubscriptions = null;
		$this->collUsergroupHasChiefs = null;
		$this->collUsergroupHasUsers = null;
		$this->collUserHasRoles = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseUser:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseUser::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


